import axios from 'axios';

const apiUrl = "http://localhost:5168/"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}`)    
    return result.data;
  },

  addTask: async (name) => {
    console.log('addTask', name);
    try {
        const response = await axios.post(`${apiUrl}/items`, { name });
        return response.data;
    } catch (error) {
        console.error('Error adding task:', error);
        throw error;
    }
},


  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete });
    try {
        const response = await axios.put(`${apiUrl}/items/${id}`, { isComplete: isComplete });
        return response.data;
    } catch (error) {
        console.error('Error setting task completion status:', error);
        throw error;
    }
},

deleteTask: async (id) => {
  console.log('deleteTask', id);
  try {
      const response = await axios.delete(`${apiUrl}/items/${id}`);
      return response.data;
  } catch (error) {
      console.error('Error deleting task:', error);
      throw error;
  }
},

};
